#!/usr/bin/env bash
set -ex

echo "__ Install httpd and php..."
sudo yum install -y httpd
sudo yum install -y php php-gd php-mbstring php-pdo php-mysqlnd

echo "<IfModule mod_dir.c>
        DirectoryIndex index.php index.html
</IfModule>" | sudo tee /etc/httpd/conf.modules.d/dir.conf

echo "  <html>
  <head>
    <title>TECC Test Web Application</title>
  </head>
  <body>
   <h1>TECC Test Web Application</h1>
  <?php
  try {
    $dbh = new PDO('mysql:host=DATABASE_ADDRESS;dbname=webapp', 'webapp_user', 'end-user-provided-password', array(
      PDO::ATTR_PERSISTENT => true,
      PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
    ));

    // ensure there is at least 1 row of data for the app to read
	$data = $dbh->query("INSERT INTO tecc_dba_test (id, time, comment) VALUES (NULL, CURRENT_TIMESTAMP, "$_SERVER['SCRIPT_NAME']")");

    $data = $dbh->query('SELECT MAX(id) AS count FROM tecc_dba_test')->fetchAll(PDO::FETCH_COLUMN);
    $count = $data[0]; //print_r($data);
    $data = $dbh->query("SELECT time, comment FROM tecc_dba_test WHERE id = $count")->fetchAll(PDO::FETCH_ASSOC);
    $time = $data[0]['time'];
    $comment = $data[0]['comment'];

    echo "<p>Database records: $count</p>\n";
    echo "<p>Last comment: $comment, on: $time</p>\n";
    echo "<h3>Web docroot: $_SERVER['DOCUMENT_ROOT'], file: $_SERVER['SCRIPT_NAME']</h3>\n";

    $dbh = null;
  }
  catch (PDOException $e) {
    print "Error!: " . $e->getcomment() . "<br/>";
    print phpinfo();
    die();
  }
  ?></body>
  </html>" | sudo tee /var/www/html/index.php

sudo systemctl enable httpd

# temporarily disable SELinux to prevent httpd to MySQL authentication issues
# this is for testing only and should not be done in production environments
echo "__ Temporarily disable SELinux..."
sudo setenforce 0

#!/usr/bin/env bash
set -ex

# Assuming CentOS/RedHat 8.2
echo "__ Mysql installation..."
# https://dev.mysql.com/doc/refman/8.0/en/linux-installation-yum-repo.html

sudo yum install -y "http://repo.mysql.com/mysql80-community-release-el8.rpm" &&
  sudo yum module -y disable mysql &&
  sudo yum install -y mysql-community-server

# database must be started for SQL configuration
sudo systemctl start mysqld
# database should start on OS boot
sudo systemctl enable mysqld
# sudo systemctl status mysqld.service

echo "__ Altering database root password..."

mysql --user=root --connect-expired-password \
  --password="$(sudo grep -oP 'temporary password(.*): \K(\S+)' /var/log/mysqld.log)" <<- EOF

SET GLOBAL validate_password.policy=LOW;
ALTER user 'root'@'localhost' IDENTIFIED BY '@@{MYSQL_PASSWORD}@@';

FLUSH PRIVILEGES;
EOF

echo "__ Creating database, table, and app user permissions..."

mysql --user=root --password='@@{MYSQL_PASSWORD}@@' <<- "EOF"

CREATE DATABASE IF NOT EXISTS webapp ;
CREATE TABLE IF NOT EXISTS webapp.tecc_dba_test ( \
  id INT NOT NULL AUTO_INCREMENT , \
  time DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , \
  comment VARCHAR(80) NULL DEFAULT NULL , \
  PRIMARY KEY (id)) \
  ENGINE = InnoDB CHARSET=ascii COLLATE ascii_general_ci COMMENT = 'testing';

FLUSH PRIVILEGES;
CREATE USER 'webapp_user'@'%' IDENTIFIED WITH mysql_native_password BY 'end-user-provided-password' \
  REQUIRE NONE WITH MAX_QUERIES_PER_HOUR 0 MAX_CONNECTIONS_PER_HOUR 0 MAX_UPDATES_PER_HOUR 0 MAX_USER_CONNECTIONS 0;
GRANT SELECT, INSERT ON *.* TO 'webapp_user'@'%';

CREATE USER 'webapp_user'@'localhost' IDENTIFIED WITH mysql_native_password BY 'end-user-provided-password' \
  REQUIRE NONE WITH MAX_QUERIES_PER_HOUR 0 MAX_CONNECTIONS_PER_HOUR 0 MAX_UPDATES_PER_HOUR 0 MAX_USER_CONNECTIONS 0;
GRANT SELECT, INSERT ON *.* TO 'webapp_user'@'localhost';

FLUSH PRIVILEGES;
EOF

